import axios from "axios";
import React from "react";
import { NavLink } from "react-router-dom";
import Footer from "../component/Footer";
import Header from "../component/Header";

interface Users {
  _id: string;
  firstName: string;
  lastName: string;
  email: string;
  password: string;
  birthday: number;
  id_profiles: string;
  is_admin: boolean;
}

function AdminPage() {
  // get all users

  const [users, setUsers] = React.useState<Users[]>([]);

  // get all users using axios

  React.useEffect(() => {
    axios.get("http://localhost:5000/api/user").then((response) => {
      setUsers(response.data);
      console.log(response.data);
    });
  }, []);

  // delete user

  function deleteUser(id: string) {
    axios.delete(`http://localhost:5000/api/user/${id}`).then((response) => {
      alert(response.data);
    });
  }

  function ifItsTrue(is_admin: boolean) {
    if (is_admin === true) {
      return "Yes";
    } else {
      return "No";
    }
  }

  // display all users

  function displayUsers() {
    return users.map((user, index) => {
      return (
        <div className="userCard" key={index}>
          <div>
            <p>First Name : </p>
            <p className="userCard-firstname">{user.firstName}</p>
          </div>
          <div>
            <p>Last Name : </p>
            <p className="userCard-lastname">{user.lastName}</p>
          </div>
          <div>
            <p>Email : </p>
            <p className="userCard-email">{user.email}</p>
          </div>
          <p className="userCard-age">Age : {user.birthday}</p>
          <p className="userCard-admin">Admin : {ifItsTrue(user.is_admin)}</p>
          <NavLink className="userCard-more" to={`/user/${user._id}`}>
            More
          </NavLink>
          <button
            className="userCard-delete"
            onClick={() => deleteUser(user._id)}
          >
            Delete
          </button>
        </div>
      );
    });
  }

  // navigate to single user page

  return (
    <>
      <Header></Header>
      <h1>Admin Page</h1>
      <main className="content-container content-container-admin">
        {displayUsers()}
      </main>
      <Footer></Footer>
    </>
  );
}

export default AdminPage;
