import axios from "axios";
import React from "react";
import { NavLink } from "react-router-dom";
import Footer from "../component/Footer";
import Header from "../component/Header";

interface Users {
  _id: string;
  firstName: string;
  lastName: string;
  email: string;
  password: string;
  birthday: number;
  id_profiles: string;
  is_admin: boolean;
}

function AdminPage() {
  // get all users

  const [user, setUser] = React.useState<Users>({
    _id: "",
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    birthday: 0,
    id_profiles: "",
    is_admin: false,
  });

  const [users, setUsers] = React.useState<Users[]>([]);

  // get all users using axios

  React.useEffect(() => {
    axios.get("http://localhost:5000/api/user").then((response) => {
      setUsers(response.data);
      console.log(response.data);
    });
  }, []);

  // delete user

  function deleteUser(id: string) {
    axios.delete(`http://localhost:5000/api/user/${id}`).then((response) => {
      console.log(response.data);
    });
  }

  // display all users

  function displayUsers() {
    return users.map((user, index) => {
      return (
        <div className="userCard" key={index}>
          <p className="userCard-firstname">First Name : {user.firstName}</p>
          <p className="userCard-lastname"> Last Name : {user.lastName}</p>
          <p className="userCard-email">E-mail : {user.email}</p>
          <p className="userCard-age">Age : {user.birthday}</p>
          <NavLink to={`/user/${user._id}`}>More</NavLink>;
          <button
            className="userCard-delete"
            onClick={() => deleteUser(user._id)}
          >
            Delete
          </button>
        </div>
      );
    });
  }

  // navigate to single user page

  return (
    <>
      <Header></Header>
      <h1>Admin Page</h1>
      <main className="content-container content-container-admin">
        {displayUsers()}
      </main>
      <Footer></Footer>
    </>
  );
}

export default AdminPage;
